mapper.select:
SELECT base_soil.*,co.COUNTY_NAME,co.city_code,co.city_name,SUBSTR(base_soil.scccode,1,2) AS scc1,SUBSTR(base_soil.scccode,3,2) AS scc2,
SUBSTR(base_soil.scccode,5,3) AS scc3,SUBSTR(base_soil.scccode,8,3) AS scc4
FROM base_soil
LEFT JOIN (
  SELECT county.COUNTY_ID,county.COUNTY_NAME,city.city_name,city.city_code
  FROM county
  LEFT JOIN city
  ON county.city_code=city.city_code
) co
ON co.COUNTY_ID=base_soil.county_id

  
    base_soil.year = #{year}
  
  
    AND base_soil.county_id = #{countyId}
  
  
    AND co.city_code = #{cityCode}
