mapper.select:
SELECT deoil.*,co.COUNTY_NAME,co.city_code,co.city_name
FROM deoil
INNER JOIN (
SELECT county.COUNTY_ID,county.COUNTY_NAME,city.city_name,city.city_code
FROM county
LEFT JOIN city
ON county.city_code=city.city_code
) co
ON co.COUNTY_ID=deoil.county_id

  
    deoil.year = #{year}
  
  
    AND deoil.county_id = #{countyId}
  
  
    AND co.city_code = #{cityCode}
