mapper.select:
SELECT * FROM ( SELECT factory.*, county.COUNTY_NAME, county.city_code, city.city_name, `status`.introduction AS statusdec, industry_big.industry_name AS industryBigName, industry_small.industry_name AS industrySmallName FROM factory LEFT JOIN county ON factory.county_id=county.county_id LEFT JOIN city ON county.city_code=city.city_code LEFT JOIN `status` ON factory.`status`=`status`.id LEFT JOIN industry_big ON factory.industry_bigid=industry_big.industry_code LEFT JOIN industry_small ON factory.industry_id=industry_small.industry_small AND factory.industry_bigid=industry_small.industry_big ) factory
year = #{year}
AND factory_id = #{factoryId}
AND county_id = #{countyId}
AND city_code = #{cityCode}
AND status = #{status}
AND factory_name LIKE CONCAT(CONCAT('%',#{factoryName},'%'))
AND factory_no LIKE CONCAT(CONCAT('%',#{factoryNo},'%'))
ORDER BY factory.factory_id ASC

SELECT COUNT(factory.factory_id) AS value,`status`.introduction AS name,factory.`status` AS code FROM factory LEFT JOIN county ON factory.county_id=county.county_id LEFT JOIN city ON county.city_code=city.city_code INNER JOIN status ON factory.`status`=`status`.id
factory.county_id = #{countyId}
AND county.city_code= #{cityCode}
AND factory.status < #{status}
GROUP BY factory.`status`

SELECT COUNT(*) AS value,industry_big.industry_name AS name FROM factory LEFT JOIN county ON factory.county_id=county.county_id LEFT JOIN city ON county.city_code=city.city_code INNER JOIN industry_big ON industry_big.industry_code=factory.industry_bigid
factory.county_id = #{countyId}
AND county.city_code = #{cityCode}
GROUP BY factory.industry_bigid

mapper.update:
update user
set password = #{password,jdbcType=VARCHAR}
where fac_no = #{facNo,jdbcType=VARCHAR}
