mapper.select:
SELECT ny_free_stockbreeding.*,co.COUNTY_NAME,co.city_code,co.city_name,SUBSTR(ny_free_stockbreeding.scccode,1,2) AS scc1,SUBSTR(ny_free_stockbreeding.scccode,3,2) AS 		scc2,SUBSTR(ny_free_stockbreeding.scccode,5,3) AS scc3,SUBSTR(ny_free_stockbreeding.scccode,8,3) AS scc4
FROM ny_free_stockbreeding
LEFT JOIN (
  SELECT county.COUNTY_ID,county.COUNTY_NAME,city.city_name,city.city_code
  FROM county
  LEFT JOIN city
  ON county.city_code=city.city_code
) co
ON co.COUNTY_ID=ny_free_stockbreeding.county_id

  
    ny_free_stockbreeding.year = #{year}
  
  
    AND ny_free_stockbreeding.county_id = #{countyId}
  
  
    AND co.city_code = #{cityCode}
