mapper.select:
SELECT oil_source.*,company.*,co.COUNTY_NAME,co.city_code,co.city_name,SUBSTR(oil_source.scccode,1,2) AS scc1, SUBSTR(oil_source.scccode,3,2) AS scc2,SUBSTR(oil_source.scccode,5,3) AS scc3,SUBSTR(oil_source.scccode,8,3) AS scc4 FROM oil_source LEFT JOIN company ON oil_source.company_id=company.com_id LEFT JOIN ( SELECT county.COUNTY_ID,county.COUNTY_NAME,city.city_name,city.city_code FROM county LEFT JOIN city ON county.city_code=city.city_code ) co ON co.COUNTY_ID=company.county_id
oil_source.year = #{year}
AND company.county_id = #{countyId}
AND co.city_code = #{cityCode}
