mapper.select:
SELECT * FROM ( SELECT gy_sh_gasemission.*,company.*,co.COUNTY_NAME,co.city_code,co.city_name,SUBSTR(gy_sh_gasemission.scccode,1,2) AS scc1, SUBSTR(gy_sh_gasemission.scccode,3,2) AS scc2,SUBSTR(gy_sh_gasemission.scccode,5,3) AS scc3,SUBSTR(gy_sh_gasemission.scccode,8,3) AS scc4 FROM gy_sh_gasemission LEFT JOIN company ON gy_sh_gasemission.factory_id=company.com_id LEFT JOIN ( SELECT county.COUNTY_ID,county.COUNTY_NAME,city.city_name,city.city_code FROM county LEFT JOIN city ON county.city_code=city.city_code ) co ON co.COUNTY_ID=company.county_id ) SS
year = #{year}
AND county_id = #{countyId}
AND city_code = #{cityCode}
