mapper.select:
  SELECT scattered_coal.*,COUNTY_NAME
  FROM scattered_coal
  INNER JOIN (
  SELECT COUNTY_ID,COUNTY_NAME,city_name FROM county
  LEFT JOIN city
  ON county.city_code=city.city_code
  ) co
  ON co.COUNTY_ID=scattered_coal.county_id

  
    scattered_coal.year = #{year}
  
  
    AND scattered_coal.county_id = #{countyId}
