mapper.select:
SELECT xjcydust_load.*,company.*,co.COUNTY_NAME,co.city_code,co.city_name,SUBSTR(xjcydust_load.scccode,1,2) AS scc1, SUBSTR(xjcydust_load.scccode,3,2) AS scc2,SUBSTR(xjcydust_load.scccode,5,3) AS scc3,SUBSTR(xjcydust_load.scccode,8,3) AS scc4 FROM xjcydust_load LEFT JOIN company ON xjcydust_load.com_id=company.com_id LEFT JOIN ( SELECT county.COUNTY_ID,county.COUNTY_NAME,city.city_name,city.city_code FROM county LEFT JOIN city ON county.city_code=city.city_code ) co ON co.COUNTY_ID=company.county_id
xjcydust_load.year = #{year}
AND company.county_id = #{countyId}
AND co.city_code = #{cityCode}
